import React from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Menu from "./components/Menu/Menu";
import friends from "./friends.json";
import Titles from "./components/TitleDiv/TitleD"
import Footer from "./components/Footer/footer"
import "./App.css";


class App extends React.Component {
  state = {
    friends,
    counter:0,
    times: 0,
    topScore:0
  };

  removeFriend = id => {
    const friends = this.state.friends.reverse(friend => friend.id !== id);
    this.setState({ friends });
  };

  MakeScore = () => {

    this.setState({ counter: this.state.counter+1 });
  };
  allowTimes = () => {
    var ids = () => {
      for(var i=0; i < this.state.friends.length; i++){
        console.log(this.state.friends[i]);
      }
    };
    console.log(ids);

    this.setState({ times: this.state.times+1 });

    // if(this.state.times >= 1){
    //   alert("You can only click once in the same character!");
    //   this.setState({ counter: 0 });
    //   this.setState({ times: 0 });
    // }
    // if(this.state.times === 11){
    //   alert("Congrats! You won")
    // }
  };




  render() {
    return (
      <Wrapper>
        <Menu score={this.state.counter}/>
        <Titles />
        {
          this.state.friends.map(friend => {
            return (<FriendCard
              removeFriend={this.removeFriend}
              MakeScore={this.MakeScore}
              allowTimes={this.allowTimes}
              id={friend.id}
              key={friend.id}
              name={friend.name}
              image={friend.image}
            />)
          })

        }
        <Footer />
      </Wrapper>
    );
  }

}

export default App;
